import { useState } from "react";




export default function Row({onClickLoading, onClickClear, onClickGroup}) {

  const [tag, setTag] = useState();
  const [groups, setGroups]= useState(false);

  const handleChangeTag= event => {
    setTag(event.target.value);
  };


  const handleClick = () => {
    if (tag==undefined){
      alert("заполните поле 'тег'")
    }else{
    onClickLoading(tag);}
  };

  const handleClickGroup = ()=>{
    onClickGroup()
    setGroups(!groups)
  }

  const handleClickClear = ()=>
  {
    onClickClear()
  }

    return (
        <div>
        <input type="text" name="search" onChange={handleChangeTag} />
        <button onClick={handleClick}>Загрузить</button>
        <button onClick={handleClickClear}>Очистить</button>
    <button onClick={handleClickGroup}>{groups ? "Разгруппировать":"Группировать"}</button>
    {groups}

      </div>
    );
  }
