import Button from 'react-bootstrap/Button';

export default function Row({
  onClickLoading,
  onClickClear,
  onClickGroup,
  group,
  tag,
  setTag,
  loading,
  setLoading
}) {
  const handleChangeTag = event => {
    setTag(event.target.value);
  };

  const handleClick = () => {
    if (tag == "") {
      alert("заполните поле 'тег'");
    } else {
      onClickLoading(tag);
      setLoading("disabled");
      setTag("");
    }
  };

  const handleClickClear = () => {
    onClickClear();
    setTag("");
  };

  return (
    <div>
      <input
        type="text"
        name="tag"
        placeholder="Введите тег"
        value={tag}
        onChange={handleChangeTag}
      />
      <button variant="primary" disabled={loading} onClick={handleClick}>
        {loading == "" ? "Загрузить" : "Загрузка..."}
      </button>
      <button onClick={handleClickClear}>Очистить</button>
      <button onClick={onClickGroup}>
        {group ? "Разгруппировать" : "Группировать"}
      </button>
    </div>
  );
}
