import Row from "../components/row";
import Card from "../components/card";
import { useState, useMemo, useEffect } from "react";

export default function Index() {
  const [url, setUrl] = useState(
    "https://api.giphy.com/v1/gifs/random?api_key=dc6zaTOxFJmzC&tag=mouse"
  );
  const [resItem, setResItem] = useState({});
  const [item, setItem] = useState([]);
  let items = [];

  const handleClickLoading = () => {
    fetch(url)
    .then(response => response.json())
    .then(data => setResItem(data))

  };

  useMemo(() => {
    if ("data" in resItem){
    setItem({"type":"cat","url":resItem.data.embed_url})
  } },[resItem])

  useMemo(() => {
    items.push(item)
   },[item])

  const handleClickClear = () => {
    alert("Clear");
  };
  const handelClickGroup = () => {
    alert("Group");
  };


  const renderCards = () => {
    if(items>0){
        return (
      <Card item={items}/>)
    }
  };

  return (
    <div>
      <Row
        onClickLoading={handleClickLoading}
        onClickClear={handleClickClear}
        onClickGroup={handelClickGroup}
      />
      {renderCards()}
      {console.log(items)}
    </div>
  );
}
