import Row from "../components/row";
import Card from "../components/card";
import { useState, useMemo, useEffect } from "react";

export default function Index() {
  const [url, setUrl] = useState(
    "https://api.giphy.com/v1/gifs/random?api_key=dc6zaTOxFJmzC&tag=mouse"
  );
  const [resItem, setResItem] = useState({});
  const [loadTag, setLoadTeg] = useState();
  const [items, setItems] = useState([]);
  const [tags, setTags] = useState([]);
  const [group, setGroup] = useState(false);

  const handleClickLoading = tag => {
    fetch(
      "https://api.giphy.com/v1/gifs/random?api_key=dc6zaTOxFJmzC&tag=" + tag
    )
      .then(response => response.json())
      .then(data => {
        if ("data" in data) {
            if ("embed_url" in data.data){
          setItems([...items, { tag: tag, url: data.data.embed_url }]);
          addTag(tag);}
         else {
            alert('По тегу ничего не найдено')
        }}else {allert('Произошла http ошибка')}
      });
  };

  const addTag = tag => {
    if (!tags.includes(tag)) {
      setTags([...tags, tag]);
    }
  };

  const handleClickClear = () => {
    setItems([]);
  };
  const handelClickGroup = () => {
    setGroup(!group);
  };

  const renderCards = () => {
    if (items.length > 0) {
      return items.map(item => <Card item={item} key={item.url} />);
    }
  };

  const renderCardsGroup = () => {
    if (items.length > 0) 
        return (tags.map(tag => 
          <div key={tag}> 
            {tag}
            {items.map(item => {
              if (tag === item.tag) {
                  return(
                <Card item={item} key={item.url} />);
              }
            })}
          </div>
      ));

  };

  return (
    <div>
      <Row
        onClickLoading={handleClickLoading}
        onClickClear={handleClickClear}
        onClickGroup={handelClickGroup}
        grour={group}
      />
      {group? renderCardsGroup():renderCards()}
      {console.log(items)}
      {console.log(tags)}
    </div>
  );
}
