import { useState } from "react";




export default function Row({onClickLoading, onClickClear, onClickGroup, group}) {

  const [tag, setTag] = useState("");

  const handleChangeTag= event => {
    setTag(event.target.value);
  };


  const handleClick = () => {
    if (tag==undefined){
      alert("заполните поле 'тег'")
    }else{
    onClickLoading(tag);}
  };



  const handleClickClear = ()=>
  {
    onClickClear()
    setTag("")
  }

    return (
        <div>
        <input type="text" name="tag" value={tag} onChange={handleChangeTag} />
        <button onClick={handleClick}>Загрузить</button>
        <button onClick={handleClickClear}>Очистить</button>
    <button onClick={onClickGroup}>{group ? "Разгруппировать":"Группировать"}</button>
      </div>
    );
  }
