import Row from "../components/row";
import Card from "../components/card";
import { useState, useMemo, useEffect } from "react";

export default function Index() {
  const [url, setUrl] = useState(
    "https://api.giphy.com/v1/gifs/random?api_key=dc6zaTOxFJmzC&tag=mouse"
  );
  const [resItem, setResItem] = useState({});
  const [item, setItem] = useState();
  const [items, setItems] = useState([]);

  const handleClickLoading = () => {
    fetch(url)
    .then(response => response.json())
    .then(data => setResItem(data))

  };

  useMemo(() => {
    if ("data" in resItem){
    setItem({"type":"mouse","url":resItem.data.embed_url})
  } },[resItem])

  useMemo(() => {
      if ("url" in items[0]){
    setItems([...items, item])}
    else {setItems([item]) }
   },[item])

  const handleClickClear = () => {
    alert("Clear");
  };
  const handelClickGroup = () => {
    alert("Group");
  };


  const renderCards = () => {
    if(items.length>0){
        return (
      <div>12345678</div>)
    }
  };

  return (
    <div>
      <Row
        onClickLoading={handleClickLoading}
        onClickClear={handleClickClear}
        onClickGroup={handelClickGroup}
      />
      {renderCards()}
      {console.log(items)}
      {console.log("url" in items)}

    </div>
  );
}
