import Card from "../components/card";
import { useState, useMemo, useEffect } from "react";

export default function Index() {
  const [url, setUrl] = useState(
    "https://api.giphy.com/v1/gifs/random?api_key=dc6zaTOxFJmzC&tag=mouse"
  );
  const [resItem, setResItem] = useState({});
  const [items, setItems] = useState();
  let item=[];

  const handleClickLoading = () => {
    fetch(url)
    .then(response => response.json())
    .then(data => setResItem(data))
  };

  useMemo(() => {
    if ("data" in resItem){
    item.push(...{"type":"cat","url":resItem.data.embed_url});
  } },[resItem])

  useMemo(() => {
    setItems(item)
   },[item])

  const handleClickClear = () => {
    alert("Clear");
  };
  const handelClickGroup = () => {
    alert("Group");
  };


//   const renderCards = () => {
//     if(items>0){
//         return (
//       <Card item={items}/>)
//     }
//   };

  return (
    <div>
      <Row
        onClickLoading={handleClickLoading}
        onClickClear={handleClickClear}
        onClickGroup={handelClickGroup}
      />
      {renderCards()}
      {console.log(items)}
    </div>
  );
}
